import { StyleSheet,Dimensions} from 'react-native';
const progressBarStyles = StyleSheet.create({
    bar:{
        borderRadius:5,
        height:10,
        color:'#003247',
        borderColor:'#003247',
        backgroundColor:'#003247',
        opacity:0.9,
    },
    text:{
        textAlign:'center',
        fontSize:24
    },
    div:{
        backgroundColor:"#fff",
        borderRadius:16,
        width:Dimensions.get('window').width - 40,
        paddingBottom:10,
        paddingTop:10,
        marginTop:15
    }
});
export {progressBarStyles};
