import React, {Component} from 'react';
import {Text, View} from 'react-native';
import * as Progress from 'react-native-progress';

export default class OnDelivery extends Component{
    state ={
        erkezik:0
    }
    fetchData= async()=>{
        const darab = await (await fetch ('http://localhost:4550/deliveryToStorage')).json();
        this.setState({erkezik:darab.number});
    }
    componentDidMount(){
        this.fetchData();
    }
    render(){
        console.log(this.state.erkezik);
        return(
            <View>
                <Text>Érkezik a raktárba {this.state.erkezik} darab eszköz</Text>
            </View>
        )
    }
}
