import React, {Component} from 'react';
import {Text, View} from 'react-native';

export default class WorkersCount extends Component{
    state ={
        szam:0
    }
    fetchData= async()=>{
        const response = await fetch('http://localhost:4550/workersCount');
        const person = await response.json();
        this.setState({szam:person});
        console.log(this.state.data);
      }
      componentDidMount(){
        this.fetchData();
      }
      render() {
        return (
          <View >
            <Text>A dolgozók száma: {this.state.szam}</Text>
          </View>
        );
      }
}
