import GradientBack from '../components/linegradient/linegradent';

export default function LoginScreen({ navigation }: RootTabScreenProps<'LoginScreen'>) {
  //const [username,setUsername] = useState('');
  //const [password,setPassword] = useState('');

  const {control,handleSubmit} = useForm();

  const onLoginPressed = (data: any) =>{
    console.log(data)
    // validation 

    
    //navigation.navigate("HomeScreen");
  };

  return (
    <View style={containerStyles.container}>
      <GradientBack/>
      <LoginText/>
      <UserInput name="username"placeholder="felhasználónév" control={control} secureTextEntry={false} />
      <UserInput name="password" placeholder="jelszó" control={control} secureTextEntry={true}/>
      <LoginButton onPress={handleSubmit(onLoginPressed)}/>
    </View>
  );
}
